mapper.select:
SELECT

FROM reserve_venue_cons as a

WHERE a.id = #{id}

select a.id, a.venue_id as "venue.id",v.name as "venue.name", i.field_id as "field.id",f.name as "field.name", a.user_name as "member.name",a.cons_mobile as "member.mobile", a.cons_date as consDate,a.update_date "updateDate", a.order_price as orderPrice, a.should_price as shouldPrice, a.discount_price as discountPrice, a.cons_price as consPrice, a.pay_type as payType, a.create_by as "createBy.id", crea.name as "createBy.name", a.update_by as "updateBy.id", upda.name as "updateBy.name", checkout.name as checkoutName, i.start_time as startTime, i.end_time as endTime from reserve_venue_cons as a INNER JOIN reserve_venue_cons_item as i on (a.id = i.cons_data_id and i.del_flag='0') INNER JOIN reserve_venue as v ON (a.venue_id = v.id and v.del_flag='0') INNER JOIN reserve_field as f on (f.id = i.field_id) LEFT JOIN reserve_user as crea on (a.create_by = crea.id) LEFT JOIN reserve_user as upda on (a.update_by = upda.id) LEFT JOIN reserve_user as checkout on (a.checkout_id = checkout.id and checkout.del_flag='0') where a.del_flag = '0' and a.reserve_type = '4'
and a.pay_type = #{payType}
and a.user_name like concat('%',#{username},'%')
and (Date(a.cons_date) BETWEEN DATE (#{startDate}) AND DATE(#{endDate}))
and a.create_by = #{createBy.id}
and a.venue_id = #{venue.id}
and i.field_id = #{field.id}
${sqlMap.dsf} ORDER BY updateDate DESC

SELECT sum(c.orderPrice) AS orderPrice from(
    SELECT
    SUM(a.cons_price) AS "orderPrice"
    from reserve_venue_cons as a
    WHERE a.del_flag = '0' and a.reserve_type = '4'
    and month(a.cons_date)=month(CURDATE()) and a.tenant_id = #{tenantId}
    ${sqlMap.dsf}
    GROUP BY
    date_format(a.cons_date, '%Y-%m-%d')
    UNION
    select sum(a.collect_price) as "orderPrice"
    from reserve_venue_order a
    WHERE a.del_flag = '0' and a.tenant_id = #{tenantId}
    and month(a.update_date)=month(CURDATE())
    ${sqlMap.dsf}
) c

SELECT sum(c.orderPrice) AS orderPrice,c.updateDate from( SELECT SUM(a.cons_price) AS "orderPrice",date_format(a.cons_date,'%Y-%m-%d') AS "updateDate" from reserve_venue_cons as a WHERE a.del_flag = '0' and a.reserve_type = '4'
and a.tenant_id = #{tenantId}
and Date(a.cons_date)=DATE(CURRENT_DATE())
and a.venue_id = #{reserveVenue.id}
${sqlMap.dsf} GROUP BY date_format(a.update_date, '%Y-%m-%d') UNION select sum(a.collect_price) as "orderPrice",date_format(a.update_date,'%Y-%m-%d') AS "updateDate" from reserve_venue_order a WHERE a.del_flag = '0'
and a.tenant_id = #{tenantId}
and Date(a.update_date)=DATE(CURRENT_DATE())
${sqlMap.dsf} GROUP BY date_format(a.update_date, '%Y-%m-%d') ) c group by c.updateDate order by c.updateDate

SELECT

FROM reserve_venue_cons as a


    a.del_flag = #{DEL_FLAG_NORMAL}

        and a.tenant_id = #{tenantId}


        and a.user_name LIKE concat('%',#{userName},'%')


        and a.reserve_type = #{reserveType}


        and a.by_pc = #{byPC}

    ${sqlMap.dsf}

        and (Date(a.update_date) BETWEEN DATE (#{sqlMap.startDate}) AND DATE(#{sqlMap.endDate}))




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

SELECT

FROM reserve_venue_cons a


    a.del_flag = #{DEL_FLAG_NORMAL}

        and a.tenant_id = #{tenantId}




        ORDER BY ${page.orderBy}


        ORDER BY a.update_date DESC

mapper.update:
UPDATE reserve_venue_cons SET
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
